   FROM (((((((public.materialflowresources_resource r
     JOIN public.materialflow_location location ON ((location.id = r.location_id)))
     JOIN public.basic_product product ON ((product.id = r.product_id)))
     LEFT JOIN public.basic_additionalcode additionalcode ON ((additionalcode.id = r.additionalcode_id)))
